mapper.insert:
INSERT INTO comment (
  comment_no, content, created_day, post_no, user_no
) VALUES (
  #{comment_no}, #{content}, SYSDATE, #{post_no}, #{user_no}
)

mapper.select:
SELECT comment_no, content, created_day, post_no, user_no
FROM comment
WHERE comment_no = #{comment_no}

mapper.update:
UPDATE comment
SET content = #{content}
WHERE comment_no = #{comment_no}
