mapper.insert:
INSERT INTO reply_report(
report_no, reply_no, user_no, reason
) VALUES (
reply_report_seq.NEXTVAL,
#{reply_no}, #{user_no}, #{reason}
)

mapper.select:
SELECT COUNT(*)
FROM reply_report
WHERE reply_no = #{reply_no}
AND user_no  = #{user_no}

mapper.update:
UPDATE reply_report
SET status = 'Y'
WHERE reply_no = #{reply_no}
