mapper.insert:
INSERT INTO review (
  review_no, rating, content, created_day, user_no, spot_no
) VALUES (
  #{review_no}, #{rating}, #{content}, SYSDATE, #{user_no}, #{spot_no}
)

mapper.select:
SELECT review_no, rating, content, created_day, user_no, spot_no
FROM review
WHERE review_no = #{review_no}

mapper.update:
UPDATE review
SET rating = #{rating},
    content = #{content}
WHERE review_no = #{review_no}
